'use strict';

const variantMatcher = (name, selector) => {
  const re = new RegExp(`^(${name})[:-]`);
  return (input) => {
    const match = input.match(re);
    if (match) {
      return {
        matcher: input.slice(match[1].length + 1),
        selector
      };
    }
  };
};

exports.variantMatcher = variantMatcher;
